import json, urllib.request, re, datetime

results = self.full_execution.get("results", []) if isinstance(self.full_execution, dict) else []
prev_data = {}
for r in results:
    if r.get("action", {}).get("label") == "sample_and_score":
        try:
            p = json.loads(r.get("result", "{}"))
            prev_data = p.get("message", p) if isinstance(p, dict) else {}
        except:
            prev_data = {}

stats = prev_data.get("stats", {})
baseline = prev_data.get("baseline")

if not stats:
    print(json.dumps({"drift_status": "ERROR", "error": "No stats from sample_and_score", "llm_explanation": "", "stats": {}, "baseline": None}))
    import sys; sys.exit(0)

if baseline is None:
    print(json.dumps({
        "drift_status": "BASELINE_CREATED",
        "stats": stats,
        "baseline": None,
        "is_new_baseline": True,
        "llm_explanation": "First run - current stats saved as baseline.",
        "consecutive_stable": 0
    }))
    import sys; sys.exit(0)

mean_shift = abs(stats.get("mean_score", 0) - baseline.get("mean_score", 0))
mal_pct_shift = abs(stats.get("pct_malicious", 0) - baseline.get("pct_malicious", 0))
p95_shift = abs(stats.get("p95_score", 0) - baseline.get("p95_score", 0))
consecutive_stable = baseline.get("consecutive_stable", 0)

if mean_shift > 0.1 or mal_pct_shift > 20 or p95_shift > 0.15:
    drift_status = "SIGNIFICANT_DRIFT"
    consecutive_stable = 0
elif mean_shift > 0.05 or mal_pct_shift > 10 or p95_shift > 0.08:
    drift_status = "MINOR_DRIFT"
    consecutive_stable = 0
else:
    drift_status = "STABLE"
    consecutive_stable += 1

llm_explanation = ""
if drift_status == "SIGNIFICANT_DRIFT":
    try:
        prompt = "/no_think Analyze this ML model drift. Current stats: mean=" + str(stats.get("mean_score")) + " p95=" + str(stats.get("p95_score")) + " malicious%=" + str(stats.get("pct_malicious")) + ". Baseline: mean=" + str(baseline.get("mean_score")) + " p95=" + str(baseline.get("p95_score")) + " malicious%=" + str(baseline.get("pct_malicious")) + ". Shifts: mean=" + str(round(mean_shift, 4)) + " p95=" + str(round(p95_shift, 4)) + " mal%=" + str(round(mal_pct_shift, 2)) + ". What could cause this drift? Is retraining warranted? Be concise (3-4 sentences)."
        payload = json.dumps({
            "model": "qwen3:8b",
            "prompt": prompt,
            "system": "You are an ML operations analyst for a home SOC lab. The ML model is an XGBoost threat scorer trained on Wazuh SIEM alerts. Drift means the score distribution has shifted from the training baseline.",
            "stream": False,
            "keep_alive": "24h",
            "options": {"num_predict": 1000, "temperature": 0.3}
        }).encode()
        req = urllib.request.Request("$ollama_url/api/generate",
            data=payload, headers={"Content-Type": "application/json"}, method="POST")
        resp = urllib.request.urlopen(req, timeout=120)
        raw = json.loads(resp.read().decode()).get("response", "")
        llm_explanation = re.sub(r"<think>.*?</think>", "", raw, flags=re.DOTALL).strip()
        llm_explanation = llm_explanation.replace("\n", " ").replace("\r", "")
        if not llm_explanation:
            llm_explanation = "LLM returned empty response."
    except Exception as e:
        llm_explanation = "LLM error: " + str(e)[:100]

is_new_baseline = False
if drift_status == "STABLE" and consecutive_stable >= 7:
    is_new_baseline = True
    consecutive_stable = 0

print(json.dumps({
    "drift_status": drift_status,
    "stats": stats,
    "baseline": baseline,
    "mean_shift": round(mean_shift, 4),
    "malicious_pct_shift": round(mal_pct_shift, 2),
    "p95_shift": round(p95_shift, 4),
    "llm_explanation": llm_explanation,
    "consecutive_stable": consecutive_stable,
    "is_new_baseline": is_new_baseline
}))
