import json, urllib.request, ssl, base64

ctx = ssl.create_default_context()
ctx.check_hostname = False
ctx.verify_mode = ssl.CERT_NONE

elk_auth = base64.b64encode("$elk_user:$elk_pass".encode()).decode()
elk_headers = {"Content-Type": "application/json", "Authorization": "Basic " + elk_auth}

def elk_query(index, body):
    req = urllib.request.Request("$elk_url/" + index + "/_search",
        data=json.dumps(body).encode(), headers=elk_headers, method="POST")
    resp = urllib.request.urlopen(req, context=ctx, timeout=15)
    return json.loads(resp.read().decode())

stats = {}

# 1. honeypot-credentials: fields are credentials.username, credentials.password, source.ip
try:
    q = {
        "size": 0,
        "query": {"range": {"@timestamp": {"gte": "now-7d"}}},
        "aggs": {
            "top_usernames": {"terms": {"field": "credentials.username", "size": 10}},
            "top_passwords": {"terms": {"field": "credentials.password", "size": 10}},
            "unique_ips": {"cardinality": {"field": "source.ip"}}
        },
        "track_total_hits": True
    }
    data = elk_query("honeypot-credentials", q)
    aggs = data.get("aggregations", {})
    stats["creds"] = {
        "total_attempts": data.get("hits", {}).get("total", {}).get("value", 0),
        "unique_ips_creds": aggs.get("unique_ips", {}).get("value", 0),
        "top_usernames": [b["key"] for b in aggs.get("top_usernames", {}).get("buckets", [])],
        "top_passwords": [b["key"] for b in aggs.get("top_passwords", {}).get("buckets", [])]
    }
except Exception as e:
    stats["creds"] = {"error": str(e)[:100], "total_attempts": 0, "unique_ips_creds": 0, "top_usernames": [], "top_passwords": []}

# 2. honeypot-access: fields are host (IP), agent (user-agent string), method
try:
    q = {
        "size": 0,
        "query": {"range": {"@timestamp": {"gte": "now-7d"}}},
        "aggs": {
            "top_ips": {"terms": {"field": "host.keyword", "size": 10}},
            "top_agents": {"terms": {"field": "agent.keyword", "size": 5}},
            "unique_ips": {"cardinality": {"field": "host.keyword"}},
            "methods": {"terms": {"field": "method", "size": 5}}
        },
        "track_total_hits": True
    }
    data = elk_query("honeypot-access", q)
    aggs = data.get("aggregations", {})
    stats["access"] = {
        "total_requests": data.get("hits", {}).get("total", {}).get("value", 0),
        "unique_ips": aggs.get("unique_ips", {}).get("value", 0),
        "top_ips": [{"ip": b["key"], "count": b["doc_count"]} for b in aggs.get("top_ips", {}).get("buckets", [])[:5]],
        "top_agents": [b["key"][:60] for b in aggs.get("top_agents", {}).get("buckets", [])],
        "methods": {b["key"]: b["doc_count"] for b in aggs.get("methods", {}).get("buckets", [])}
    }
except Exception as e:
    stats["access"] = {"error": str(e)[:100], "total_requests": 0, "unique_ips": 0, "top_ips": [], "top_agents": [], "methods": {}}

# 3. honeypot-wazuh: GeoLocation.country_name, rule.mitre.technique
try:
    q = {
        "size": 0,
        "query": {"range": {"@timestamp": {"gte": "now-7d"}}},
        "aggs": {
            "top_rules": {"terms": {"field": "rule.description", "size": 10}},
            "top_mitre": {"terms": {"field": "rule.mitre.technique", "size": 10}},
            "top_countries": {"terms": {"field": "GeoLocation.country_name", "size": 10}}
        },
        "track_total_hits": True
    }
    data = elk_query("honeypot-wazuh", q)
    aggs = data.get("aggregations", {})
    stats["wazuh"] = {
        "total_alerts": data.get("hits", {}).get("total", {}).get("value", 0),
        "top_rules": [{"rule": b["key"][:60], "count": b["doc_count"]} for b in aggs.get("top_rules", {}).get("buckets", [])[:5]],
        "top_mitre": [b["key"] for b in aggs.get("top_mitre", {}).get("buckets", [])],
        "top_countries": [{"country": b["key"], "count": b["doc_count"]} for b in aggs.get("top_countries", {}).get("buckets", [])[:5]]
    }
except Exception as e:
    stats["wazuh"] = {"error": str(e)[:100], "total_alerts": 0, "top_rules": [], "top_mitre": [], "top_countries": []}

# 4. Previous week honeypot-intel doc
prev_week = None
try:
    q = {"size": 1, "sort": [{"@timestamp": {"order": "desc"}}], "query": {"match_all": {}}}
    data = elk_query("honeypot-intel", q)
    hits = data.get("hits", {}).get("hits", [])
    if hits:
        prev_week = hits[0]["_source"]
except Exception:
    pass

total_attempts = stats.get("creds", {}).get("total_attempts", 0)
unique_ips = stats.get("access", {}).get("unique_ips", 0)
unique_cred_ips = stats.get("creds", {}).get("unique_ips_creds", 0)

wow = {}
if prev_week:
    prev_attempts = prev_week.get("total_attempts", 0)
    prev_ips = prev_week.get("unique_ips", 0)
    if prev_attempts > 0:
        wow["attempt_change_pct"] = round((total_attempts - prev_attempts) / prev_attempts * 100, 1)
    if prev_ips > 0:
        wow["ip_change_pct"] = round((unique_ips - prev_ips) / prev_ips * 100, 1)

stats["summary"] = {
    "total_attempts": total_attempts,
    "unique_ips": unique_ips,
    "unique_cred_ips": unique_cred_ips,
    "wow": wow
}

print(json.dumps({"stats": stats, "prev_week": prev_week is not None}))
